#!/bin/bash

# Install software (English)
sudo apt-get install -y language-pack-en \
                                   language-pack-kde-en \
                                   kde-l10n-engb;

# Install software (French)
sudo apt-get install -y language-pack-fr \
                                   language-pack-kde-fr \
                                   kde-l10n-fr;

# Install software (Portuguese)
sudo apt-get install -y language-pack-pt \
                                   language-pack-kde-pt \
                                   kde-l10n-pt;

